#include <algorithm>
#include <iostream>
#include <vector>
#include <set>

using namespace std;

class Solution {
public:
    vector<vector<int>> threeSum(vector<int>& nums) {
        if (nums.size() == 0)
            return {};
        set<vector<int>> result_set;

        sort(begin(nums), end(nums));

        vector<int> sorted_nums;
        sorted_nums.reserve(nums.size());

        auto prev = nums[0];
        auto count = 1;
        sorted_nums.emplace_back(prev);

        for (auto i = 1; i < nums.size(); i++) {
            if (nums[i] != prev) {
                prev = nums[i];
                count = 1;
                sorted_nums.emplace_back(prev);
            }
            else {
                if (count < 3) {
                    sorted_nums.emplace_back(prev);
                    count++;
                }
            }
        }

        for (auto i = 0; i < sorted_nums.size(); i++) {
            for (auto j = i + 1; j < sorted_nums.size(); j++) {
                auto v = -sorted_nums[i] - sorted_nums[j];
                auto it = upper_bound(begin(sorted_nums), end(sorted_nums), v);

                if (it == begin(sorted_nums))
                    continue;

                it--;

                auto k = distance(begin(sorted_nums), it);
                if (k < j + 1)
                    continue;

                if (*it != v)
                    continue;

                vector<int> r{sorted_nums[i], sorted_nums[j], sorted_nums[k]};
                sort(begin(r), end(r));

                if (result_set.find(r) == end(result_set))
                    result_set.emplace(r);
            }
        }

        vector<vector<int>> result;
        result.reserve(result_set.size());
        copy(begin(result_set), end(result_set), back_inserter(result));

        return result;
    }
};

